  NODE: {
    persistent: fireauth.storage.LocalStorage,
    temporary: fireauth.storage.SessionStorage
  },
  REACT_NATIVE: {
    persistent: fireauth.storage.AsyncStorage,
    temporary: fireauth.storage.NullStorage
  },
  WORKER: {
